<?php    /*    Template Name: Архив проектов Портфолио преображения    */?><?phpget_header();$mytheme = get_option('portfolio_options');global $mytheme;?>    <div id="portfolio-content" class="block">		<div class="aligncenter zagolovok">			<h2>				<?php echo $mytheme['zagolovokportfolio']; ?>			</h2>		</div>		<div class="aligncenter slogonportfolio">			<h3>				<?php echo $mytheme['slogonportfolio']; ?>			</h3>		</div>        <?php if (have_posts()) :		while (have_posts()) : the_post(); ?>        <div id="post-<?php the_ID(); ?>" <?php post_class(); ?>>			<a class="portfolio-block" href="<?php the_permalink(); ?>">				<div class="portfolio-title aligncenter">					<h2 class="portfolios"><?php echo get_post_meta($post->ID, 'text_after', 1); ?></h2>				</div>					<div class="archive-thumb aligncenter">						<div class="before">							<?php $img_2 = get_post_meta($post->ID, 'uploader_before', 1); ?>						<img src="<?php echo wp_get_attachment_url($img_2); ?>" alt=""/>							<div class="caption_before">До</div></div>						<div class="after">						<?php						for ($i = 0; $i<=2; $i++) {							$name_meta = "uploader_after_".$i;							$imgs = get_post_meta($post->ID, $name_meta, 1);							echo '<img src="'.wp_get_attachment_url($imgs).'" alt=""/>';						}							?>                </div>					</div>			</a>			</div>    <?php endwhile; pager(); endif; ?>				<div style="clear:both;"></div>		<br />		<div class="aligncenter formslogon">			<h3>				<?php echo $mytheme['zagolovokformportfolio']; ?>			</h3>		<div class="aligncenter portfolioform form">			<?php $formsportfolio = $mytheme['formsportfolio']; echo do_shortcode($formsportfolio); ?>		</div>		</div>    </div><?php get_footer(); ?>