<?php    class ControlPanel {var $options;// Устанавливаем значения по умолчанию        function ControlPanel() {            add_action("admin_menu", array(&$this,"wardrobe_rehab_portfolio_plugin_menu"));            if (!is_array(get_option('portfolio_options')))                add_option('portfolio_options', $this->default_settings);            $this->options = get_option('portfolio_options');        }    }$cpanel = new ControlPanel();$mytheme = get_option('portfolio_options');?>